import React, { Component } from 'react';
import './App.css';
import FaceRecognition from './components/FaceRecognition/FaceRecognition.js'
import Navigation from './components/Navigation/Navigation.js'
import Logo from './components/Logo/Logo.js'
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm.js'
import Rank from './components/Rank/Rank.js'
import Particles from 'react-particles-js';
import Clarifai from 'clarifai'


//Configuration

const app = new Clarifai.App({
  apiKey: '6b750152f84f4e0a9f539eab6d2130ce'
 });

const particlesOptions = {
  particles: {
    number: {
      value: 80,
      density: {
        enable: true,
        value_area: 800
      }
    }
  }
}

//Main App render section
class App extends Component {
  constructor() {
    super();
    this.state = {
      input: '',
      imageURL:'',
      faceResults:[],
    }

  }



  retrieveEstimates = (data) => {
    const concepts = data.outputs[0].data.regions[0].data.concepts
    console.log(concepts);
    //Age part
    const ageConcepts = concepts.map((concept) => {
      if (concept.vocab_id === "age_appearance") {
          return concept
        }
      })
    const highestAge = (({ name, value }) => ({ name, value }))(ageConcepts[0]);
    //gender part
    const genderConcepts = concepts.filter((concept) => {
      return concept.vocab_id === "gender_appearance"
    })
    const masculine =concepts.filter((e) => {
      return e.name === 'masculine';
    });
    console.log(masculine);
    console.log(highestAge);
    console.log(genderConcepts);
    //
  }

  onInputChange = (event) => {
    this.setState({imageURL: event.target.value})
  }

  onSubmit = () => {
    console.log('click');
    app.models.initModel({id: Clarifai.DEMOGRAPHICS_MODEL})
      .then(Model => {
        return Model.predict(this.state.imageURL);
      })
      .then(response => {
        this.retrieveEstimates(response)
      }).catch((err) => console.log(err));
  }

  render() {
    return (
      <div className="App">
        <Particles className='particles'
          params={particlesOptions} 
        />
        <Navigation />
        <Logo />
        <Rank/>
        <ImageLinkForm 
        onInputChange={this.onInputChange} 
        onSubmit={this.onSubmit}
        /> 
        <FaceRecognition imageURL={this.state.imageURL}/>
      </div>
    );
  }
}
export default App;
